import React, { useState } from 'react';

const Tour = ({id, image, info, title}) => {
  const [tour, setTour] = useState([]);
  return(
    <div>{tour.map(()=>{
          return<article className='single-tour'>
            <img src={image} />
            <footer>
                <div className='tour-info'>
                  <h4>{title}</h4>
                  <p></p>
                </div>
                <p></p>
                <button className='delete-btn'>Not Interested</button>
            </footer>
          </article>
    })}</div>
  );
};

export default Tour;
