import React, { useState } from 'react';
import data from './data';
import SingleQuestion from './Question';
function App() {
  const question = data;

  return (
    <main className='container'>
      <div>
        <h3>Questions And Answers About Login</h3>
      </div>
     <div>
     {question.map(()=>{
        return <SingleQuestion questions={question}/>
      })}
     </div>
    </main>
  );
}

export default App;
