import React from 'react';

const Menu = ({menus}) => {
  return <section className='section-center'>
    <div>{
        menus.map((menu)=>{
          <div className='menu-item' key={menu.id}>
            <div className='photo'>
              <img src={menu.img} />
            </div>
            <div className='item-info'>
              <header>
                <h4>{menu.title}</h4>
                <p className='price'>{menu.price}</p>
              </header>
              <p className='item-text'>{menu.desc}</p>
            </div>
          </div>
        })
      }</div>
  </section>
};

export default Menu;
