import React, { useState } from 'react';
import Menu from './Menu';
import Categories from './Categories';
import items from './data';

const allCategories = new Set(['All',...items.map((item)=> item.category)]) 


function App() {
  const [menus, setMenus] = useState(items);
  const [categorys, setCategory] = useState(allCategories);

  
  const filter =(category)=>{
    const newCtg = items.filter((item)=>
      item.category === category
    )
    setMenus(newCtg)
  }

  return <main className='menu'>
    <section className='section'>
      <div  className='title'>
        <h2>Our Menus</h2>
        <div className='underline'></div>
      </div>
      <Categories buttons={filter} />
      <Menu menus={menus}/>
    </section>
  </main>
}

export default App;
