const url = 'https://course-api.com/react-tabs-project'
function App() {
  const [loading, setLoading] = useState(true);
  const [tabs, setTabs] = useState([]);

  const mydata = async ()=>{
    const res = await fetch(url);
    const retun = await res.json()
    setTabs(retun);
    setLoading(false)
  }
  mydata();

  if (loading) {
    return<div>
      <h1>Loadind...</h1>
    </div>
  }


  return(
    <section className='section'>
      <div className="title">
        <h2>Experience</h2>
        <div className='underline'></div>
      </div>
      <div>
        <div>
          {}
        </div>
      </div>
    </section>
  )
}

export default App
