import React, { useState, useEffect } from 'react';
import { FiChevronRight, FiChevronLeft } from 'react-icons/fi';
import { FaQuoteRight } from 'react-icons/fa';
import data from './data';
function App() {
  const [people, setPeople] = useState(data);
  const [index, setIndex] = useState(0);
  


  return(
    <main className='section'>
      <div className='header title'>
        <h2><span>/</span>Reviews</h2>
      </div>
      <div className='section-center'>{
        people.map((person, personIndex)=>{
          const {image, name, title, quote, id}= person;

          let position = 'activeSlider';
          if (personIndex === index) {
            position = 'activeSlider'
          }
          if (personIndex === index-1 || (index === 0 && personIndex === person.length -1) ) {
            position = 'lastSlider'
          }
          return <article key={id} className={position}>
            <img src={image} className='person-img'/>
            <h4>{name}</h4>
            <p className='title'>{title}</p>
            <p className='text'>{quote}</p>
            <FaQuoteRight className='icon'/>
          </article>
        })
      }
      <button className='prev' onClick={()=>setIndex(index-1)}><FiChevronLeft /></button>
      <button className='next' onClick={()=>setIndex(index+1)}><FiChevronRight /></button>
      </div>
    </main>
  );
}

export default App;
